pages/product:
---
product:
    videos:
        limit: {{theme_settings.productpage_videos_count}}
    reviews:
        limit: {{theme_settings.productpage_reviews_count}}
    related_products:
        limit: {{theme_settings.productpage_products_count}}
    similar_by_views:
        limit: {{theme_settings.productpage_products_count}}
products:
    featured:
        limit: {{theme_settings.sidebar_products_per_page}}
    new:
        limit: 25
    top_sellers:
        limit: {{theme_settings.sidebar_products_per_page}}
---
{{inject "productId" product.id}}

{{#partial "page"}}
<div class="page page-product" itemscope itemtype="http://schema.org/Product">
    <div class="product-view-top">
        <div class="container">
            {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}

            {{#each product.shipping_messages}}
                {{> components/common/alert-info message}}
            {{/each}}

            {{> components/products/product-view productsnew=products.new schema=true}}

            {{{region name="product_below_content"}}}
        </div>
    </div>
    <div class="product-view-bottom">
        {{> components/halothemes/products/halo-bundle-products}}

        {{> components/halothemes/products/product-view-more schema=true}}

        {{> components/halothemes/products/product-view-review schema=true}}

        {{#if theme_settings.productpage_product_block-1 '==' 'Related'}}
            {{> components/products/related products=product.related_products productsnew=products.new title=theme_settings.productpage_product_block-1-title block_id="halo-product-block-1"}}
        {{else if theme_settings.productpage_product_block-1 '==' 'Customers'}}
            {{> components/products/similar-by-view products=product.similar_by_views productsnew=products.new title=theme_settings.productpage_product_block-1-title block_id="halo-product-block-1"}}
        {{/if}}

        {{> components/halothemes/products/product-view-more-2 schema=true}}

        {{#if theme_settings.productpage_product_block-2 '==' 'Related'}}
            {{> components/products/related products=product.related_products productsnew=products.new title=theme_settings.productpage_product_block-2-title block_id="halo-product-block-2"}}
        {{else if theme_settings.productpage_product_block-2 '==' 'Customers'}}
            {{> components/products/similar-by-view products=product.similar_by_views productsnew=products.new title=theme_settings.productpage_product_block-2-title block_id="halo-product-block-2"}}
        {{/if}}
    </div>
    {{> components/halothemes/home/home-icon-with-text}}
</div>
{{/partial}}
{{> layout/base}}

components/products/related:
{{#if product.related_products}}
	<div class="halo-block halo-block-product halo-block-product2" id="halo-related-products">
		<div class="container">
			<div class="halo-block-header">
		        <h2 class="title">{{title}}</h2>
		    </div>
		    {{#if settings.data_tag_enabled}}
		    	{{> components/products/carousel products=products productsnew=productsnew block_id=block_id list="Related Products"}}
		    {{else}}
		    	{{> components/products/carousel products=products productsnew=productsnew block_id=block_id}}
		    {{/if}}
		</div>
	</div>
{{/if}}

components/products/similar-by-view:
{{#if product.similar_by_views}}
	<div class="halo-block halo-block-product halo-block-product2" id="halo-similar-products">
		<div class="container">
			<div class="halo-block-header">
		        <h2 class="title">{{title}}</h2>
		    </div>
		    {{#if settings.data_tag_enabled}}
		    	{{> components/products/carousel products=products productsnew=productsnew block_id=block_id list="Customers also viewed"}}
		    {{else}}
		    	{{> components/products/carousel products=products productsnew=productsnew block_id=block_id}}
		    {{/if}}
		</div>
	</div>
{{/if}}

